import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const PlaceOrder = () => {
  const navigate = useNavigate();
  const [security_id, setSecurityId] = useState("");
  const [exchange_segment, setExchangeSegment] = useState("NSE_EQ");
  const [side, setSide] = useState("BUY");
  const [orderType, setOrderType] = useState("LIMIT");
  const [quantity, setQuantity] = useState(1);
  const [price, setPrice] = useState("");
  const [triggerPrice, setTriggerPrice] = useState("");
  const [productType, setProductType] = useState("INTRADAY");
  const [timeInForce, setTimeInForce] = useState("GTC");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState(null);
  const [orders, setOrders] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = async () => {
    setError(null);
    setSearchResults([]);
    if (!searchQuery.trim()) {
      setError("Please enter a search term.");
      return;
    }
    try {
      const response = await axios.get(`http://localhost:5000/api/search?query=${searchQuery}`);
      if (response.data.length > 0) {
        setSearchResults(response.data);
      } else {
        setError("No matching data found.");
      }
    } catch (err) {
      setError("Error fetching search results. Please try again.");
    }
  };

  const handleRowClick = (result) => {
    setSecurityId(result.Security_ID || "");
    setExchangeSegment(result.Exchange_segment || "NSE_EQ");
  };

  const handlePlaceOrder = async () => {
    setError(null);
    setSuccessMessage(null);
    if (quantity <= 0) {
      setError("Quantity must be greater than 0.");
      return;
    }
    if (["LIMIT", "STOP_LOSS"].includes(orderType) && price <= 0) {
      setError("Price must be greater than 0.");
      return;
    }
    if (["STOP_LOSS", "STOP_LOSS_MARKET"].includes(orderType) && triggerPrice <= 0) {
      setError("Trigger price must be greater than 0.");
      return;
    }
    setLoading(true);
    let orderData = {
      security_id,
      exchange_segment,
      transaction_type: side,
      quantity,
      order_type: orderType,
      product_type: productType,
    };
    if (["LIMIT", "STOP_LOSS"].includes(orderType)) {
      orderData.price = parseFloat(price);
    }
    if (["STOP_LOSS", "STOP_LOSS_MARKET"].includes(orderType)) {
      orderData.trigger_price = parseFloat(triggerPrice);
    }
    if (orderType === "LIMIT") {
      orderData.timeInForce = timeInForce;
    }
    try {
      const response = await axios.post("http://localhost:5000/api/place-order", orderData, {
        headers: {
          "Content-Type": "application/json",
        },
      });
      setOrders((prevOrders) => [...prevOrders, response.data]);
    } catch (err) {
      setError("Error placing order. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-gray-800 p-6 rounded-lg shadow-lg text-white max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Place Order</h2>
      <div className="mb-4 flex">
        <input type="text" placeholder="Search..." value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} className="w-full p-2 rounded bg-gray-700 border border-gray-600" />
        <button onClick={handleSearch} className="ml-1 p-1 bg-green-500 text-black font-bold rounded">Search</button>
      </div>
      {searchResults.length > 0 && (
        <div className="mt-4">
          <h3 className="text-xl font-bold mb-2">Search Results</h3>
          <table className="w-full border border-gray-600">
            <thead>
              <tr className="bg-gray-700 text-white">
                <th>Exchange Segment</th>
                <th>Security ID</th>
                <th>Symbol Name</th>
              </tr>
            </thead>
            <tbody>
              {searchResults.map((result, index) => (
                <tr key={index} onClick={() => handleRowClick(result)} className="bg-gray-800 text-white cursor-pointer">
                  <td>{result.Exchange_segment || "N/A"}</td>
                  <td>{result.Security_ID || "N/A"}</td>
                  <td>{result.Symbol_Name || "N/A"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
      <div>
        <label>Security ID:</label>
        <input type="text" value={security_id} onChange={(e) => setSecurityId(e.target.value)} className="w-full p-2 rounded bg-gray-700 border border-gray-600" />
      </div>
      <div>
        <label>Exchange Segment:</label>
        <input type="text" value={exchange_segment} onChange={(e) => setExchangeSegment(e.target.value)} className="w-full p-2 rounded bg-gray-700 border border-gray-600" />
      </div>
      <button onClick={handlePlaceOrder} disabled={loading} className="w-full p-2 mt-4 bg-green-500 text-black font-bold rounded">
        {loading ? "Placing Order..." : "Place Order"}
      </button>
      {error && <p className="mt-4 p-2 bg-red-400 text-black rounded">{error}</p>}
    </div>
  );
};

export default PlaceOrder;
