import React, { useEffect, useState } from 'react';
import axios from 'axios';
import io from 'socket.io-client';

const PnLTable = () => {
    const [pnlData, setPnlData] = useState([]);
    const [socket, setSocket] = useState(null);

    useEffect(() => {
        // Fetch initial PnL data
        const fetchPnLData = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/pnl_report');
                setPnlData(response.data);
            } catch (error) {
                console.error('Error fetching PnL data:', error);
            }
        };

        fetchPnLData();

        // Set up WebSocket connection
        const newSocket = io('http://localhost:5000');
        setSocket(newSocket);

        newSocket.on('pnl_update', (data) => {
            setPnlData((prevData) => {
                const updatedData = prevData.map((item) =>
                    item.orderId === data.orderId ? { ...item, ...data } : item
                );
                return updatedData;
            });
        });

        newSocket.on('price_update', (data) => {
            setPnlData((prevData) => {
                const updatedData = prevData.map((item) =>
                    item.symbol === data.symbol ? { ...item, currentPrice: data.currentPrice } : item
                );
                return updatedData;
            });
        });

        return () => newSocket.close();
    }, []);

    return (
        <div>
            <h2>PnL Report</h2>
            <table>
                <thead>
                    <tr>
                        <th>Timestamp</th>
                        <th>Order ID</th>
                        <th>Symbol</th>
                        <th>Buy Price</th>
                        <th>Current Price</th>
                        <th>Quantity</th>
                        <th>Realized PnL</th>
                        <th>Unrealized PnL</th>
                        <th>PnL Percentage</th>
                        <th>Profit</th>
                        <th>Loss</th>
                        <th>Sell Status</th>
                    </tr>
                </thead>
                <tbody>
                    {pnlData.map((data, index) => (
                        <tr key={index}>
                            <td>{data.timestamp}</td>
                            <td>{data.orderId}</td>
                            <td>{data.symbol}</td>
                            <td>{data.buyPrice}</td>
                            <td>{data.currentPrice}</td>
                            <td>{data.quantity}</td>
                            <td>{data.realizedPnL}</td>
                            <td>{data.unrealizedPnL}</td>
                            <td>{data.pnlPercentage}</td>
                            <td>{data.profit}</td>
                            <td>{data.loss}</td>
                            <td>{data.sellStatus}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default PnLTable;




