import { useState } from "react";
import { useNavigate } from "react-router-dom"; // Import navigation hook
import { Home, ClipboardList, Settings, User, ChevronDown } from "lucide-react";

export default function Sidebar({ setSelectedOrder }) {
  const [showOrders, setShowOrders] = useState(false);
  const [showSpotOptions, setShowSpotOptions] = useState(false);
  const navigate = useNavigate(); // Hook for navigation

  return (
    <div className="w-64 p-4 border-r border-gray-700 flex flex-col gap-4">
      <nav className="flex flex-col gap-2">
        <button className="flex items-center gap-2 p-2 rounded-lg bg-gray-800"><Home /> Dashboard</button>

        {/* Orders Dropdown */}
        <div>
          <button
            className="flex items-center justify-between w-full p-2 rounded-lg hover:bg-gray-800"
            onClick={() => setShowOrders(!showOrders)}
          >
            <span className="flex items-center gap-2"><ClipboardList />Orders</span>
            <ChevronDown className={`transition-transform ${showOrders ? "rotate-180" : ""}`} />
          </button>

          {showOrders && (
            <div className="ml-6 mt-2 flex flex-col gap-2">
              <button
                className="p-2 rounded-lg hover:bg-gray-700"
                onClick={() => setShowSpotOptions(!showSpotOptions)}
              >
                Spot Order
              </button>

              {showSpotOptions && (
                <div className="ml-6 mt-2 flex flex-col gap-2">
                  <button
                    className="p-2 rounded-lg hover:bg-gray-600"
                    onClick={() => navigate('/place-order')} // Navigate to PlaceOrder
                  >
                    Create Order
                  </button>
                </div>
              )}
              {showSpotOptions && (
                <div className="ml-6 mt-2 flex flex-col gap-2">
                  <button
                    className="p-2 rounded-lg hover:bg-gray-600"
                    onClick={() => navigate('/pnl-report')} // Navigate to PlaceOrder
                  >
                    PNL Report
                  </button>
                </div>
              )}
              {showSpotOptions && (
                <div className="ml-6 mt-2 flex flex-col gap-2">
                  <button
                    className="p-2 rounded-lg hover:bg-gray-600"
                    onClick={() => navigate('/modify-order')} // Navigate to PlaceOrder
                  >
                    Order Modify
                  </button>
                </div>
              )}
              {showSpotOptions && (
                <div className="ml-6 mt-2 flex flex-col gap-2">
                  <button
                    className="p-2 rounded-lg hover:bg-gray-600" 
                    onClick={() => navigate('/cancel-order')} // Navigate to PlaceOrder
                  >
                    Order Cancel
                  </button>
                </div>
              )}
            </div>
          )}
        </div>
        {/* Orders Dropdown */}
        <div>
          <button
            className="flex items-center justify-between w-full p-2 rounded-lg hover:bg-gray-800"
            onClick={() => setShowOrders(!showOrders)}
          >
            <span className="flex items-center gap-2"><ClipboardList />User_Data_History</span>
            {/* <ChevronDown className={`transition-transform ${showOrders ? "rotate-180" : ""}`} /> */}
          </button>

          {showOrders && (
            <div className="ml-6 mt-2 flex flex-col gap-2">
              <button
                className="p-2 rounded-lg hover:bg-gray-700"
                onClick={() => setShowSpotOptions(!showSpotOptions)}
              >
                Spot Order
              </button>

              {showSpotOptions && (
                <div className="ml-6 mt-2 flex flex-col gap-2">
                  <button
                    className="p-2 rounded-lg hover:bg-gray-600"
                    onClick={() => navigate('/place-order')} // Navigate to PlaceOrder
                  >
                    Create Order
                  </button>
                </div>
              )}
              
            </div>
          )}
        </div>
        <button className="flex items-center gap-2 p-2 rounded-lg bg-gray-800"><User />User_Data_History</button>
          

        <button className="flex items-center gap-2 p-2 rounded-lg hover:bg-gray-800"><User /> Account</button>
        <button className="flex items-center gap-2 p-2 rounded-lg hover:bg-gray-800"><Settings /> Settings</button>
      </nav>
    </div>
  );
}
