import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from "react-router-dom";

const PnLReport = () => {
  const navigate = useNavigate();
  const [orderId, setOrderId] = useState('');
  const [pnlData, setPnlData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [showPopup, setShowPopup] = useState(false); // Popup state

  const fetchPnLReport = async () => {
    if (!orderId.trim()) {
      setError("Order ID is required.");
      return;
    }

    setLoading(true);
    setError('');
    setShowPopup(false); // Hide popup initially

    try {
      const response = await axios.get(`http://localhost:5000/api/pnl_report?orderId=${orderId}`);
      setPnlData(response.data);

      // Show popup message
      setShowPopup(true);
      setTimeout(() => setShowPopup(false), 3000); // Hide after 3 seconds

      setOrderId(''); // Clear input field
    } catch (err) {
      setError("Error fetching data from the backend.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-gray-800 p-6 rounded-lg shadow-lg text-white max-w-md mx-auto">
      <h1 className="text-2xl font-bold mb-4">Binance Order PnL Report</h1>
      
      <input 
        type="text"
        placeholder="Enter Order ID"
        value={orderId}
        onChange={(e) => setOrderId(e.target.value)}
        className="w-full p-2 rounded bg-gray-700 border border-gray-600"
      />

      <button 
        onClick={fetchPnLReport} 
        disabled={loading}
        className="w-full p-2 mt-3 bg-yellow-500 text-black font-bold rounded hover:bg-yellow-600"
      >
        {loading ? 'Loading...' : 'Get PnL Report'}
      </button>

      <button
        onClick={() => navigate("/")}
        className="w-full p-2 mt-3 bg-gray-500 text-white rounded hover:bg-gray-600"
      >
        Back to Dashboard
      </button>

      {error && <div className="mt-4 p-2 bg-red-600 text-white rounded">{error}</div>}

      {/* Popup Message */}
      {showPopup && (
        <div className="fixed top-10 right-10 bg-green-500 text-white p-3 rounded-lg shadow-lg">
          PnL Report Fetched Successfully!
        </div>
      )}

      {pnlData && (
        <div className="mt-4 p-4 bg-gray-700 rounded-lg">
          <h2 className="text-xl font-bold">Order PnL Details</h2>
          <p><strong>Order ID:</strong> {pnlData.orderId}</p>
          <p><strong>Symbol:</strong> {pnlData.symbol}</p>
          <p><strong>Buy Price:</strong> {pnlData.buyPrice}</p>
          <p><strong>Current Price:</strong> {pnlData.currentPrice}</p>
          <p><strong>Quantity:</strong> {pnlData.quantity}</p>
          <p><strong>Realized PnL:</strong> {pnlData.realizedPnL}</p>
          <p><strong>Unrealized PnL:</strong> {pnlData.unrealizedPnL}</p>
          <p><strong>PnL Percentage:</strong> {pnlData.pnlPercentage}%</p>
          <p><strong>Profit:</strong> {pnlData.profit}</p>
          <p><strong>Loss:</strong> {pnlData.loss}</p>
          <p><strong>Sell Status:</strong> {pnlData.sellStatus}</p>
        </div>
      )}
    </div>
  );
};

export default PnLReport;
