import React, { useEffect, useState } from "react";

const PnlReport = () => {
    const [pnlData, setPnlData] = useState([]);
    const [loading, setLoading] = useState(true);

    // Fetch PnL report from backend API
    const fetchPnlReport = async () => {
        try {
            const response = await fetch("http://127.0.0.1:5000/api/pnl-report");
            const data = await response.json();
            if (data.status === "success") {
                setPnlData(data.pnl_report);
            }
        } catch (error) {
            console.error("Error fetching PnL report:", error);
        } finally {
            setLoading(false);
        }
    };

    // Fetch data every 5 seconds
    useEffect(() => {
        fetchPnlReport();
        const interval = setInterval(fetchPnlReport, 500000);
        return () => clearInterval(interval);
    }, []);

    return (
        <div className="p-4">
            <h2 className="text-xl font-bold mb-4">Live PnL Report</h2>
            {loading ? (
                <p>Loading...</p>
            ) : (
                <table className="table-auto w-full border-collapse border border-green-200">
                    <thead>
                        <tr className="bg-green-100">
                            <th className="border p-2">Order ID</th>
                            <th className="border p-2">Security ID</th>
                            <th className="border p-2">Exchange</th>
                            <th className="border p-2">Type</th>
                            <th className="border p-2">Quantity</th>
                            <th className="border p-2">Order Price</th>
                            <th className="border p-2">Latest Price</th>
                            <th className="border p-2">PnL</th>
                        </tr>
                    </thead>
                    <tbody>
                        {pnlData.map((order) => (
                            <tr key={order.order_id}>
                                <td className="border p-2">{order.order_id}</td>
                                <td className="border p-2">{order.security_id}</td>
                                <td className="border p-2">{order.exchange_segment}</td>
                                <td className="border p-2">{order.transaction_type}</td>
                                <td className="border p-2">{order.quantity}</td>
                                <td className="border p-2">{order.order_price.toFixed(2)}</td>
                                <td className="border p-2">{order.latest_price.toFixed(2)}</td>
                                <td className={`border p-2 ${order.pnl >= 0 ? "text-green-600" : "text-red-600"}`}>
                                    {order.pnl.toFixed(2)}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default PnlReport;
