import React, { useState } from "react";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);
  const [message, setMessage] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null);
    setMessage("");

    const response = await fetch("http://localhost:5000/auth", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ action: "login", email, password }),
    });

    const data = await response.json();
    if (response.ok) {
      setMessage(data.message);
    } else {
      setError(data.error);
    }
  };

  const handleGoogleLogin = async () => {
    const response = await fetch("http://localhost:5000/auth", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ action: "google_login", email }),
    });

    const data = await response.json();
    if (response.ok) {
      setMessage(data.message);
    } else {
      setError(data.error);
    }
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Left Side - Login Form */}
      <div className="w-1/2 flex flex-col justify-center items-center bg-white p-10 shadow-lg">
        <h1 className="text-3xl font-bold mb-4">Log in</h1>
        <p className="text-gray-600 mb-6">Welcome back! Please enter your details.</p>

        {error && <p className="text-red-500">{error}</p>}
        {message && <p className="text-green-500">{message}</p>}

        <form onSubmit={handleLogin} className="w-full">
          <input
            type="email"
            placeholder="Enter your email"
            className="w-full p-3 mb-4 border rounded-lg"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Enter your password"
            className="w-full p-3 mb-4 border rounded-lg"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit" className="w-full p-3 bg-gradient-to-r from-orange-400 to-pink-400 text-white rounded-lg font-semibold">
            Log in
          </button>
        </form>

        <button onClick={handleGoogleLogin} className="w-full p-3 mt-3 border flex items-center justify-center rounded-lg text-gray-700">
          <img src="https://www.svgrepo.com/show/355037/google.svg" alt="Google" className="w-5 h-5 mr-2" />
          Log in with Google
        </button>

        <p className="mt-4 text-gray-600">
          Don't have an account? <a href="#" className="text-blue-500">Sign up</a>
        </p>
      </div>

      {/* Right Side - Static Image */}
      <div className="w-1/2 flex items-center justify-center bg-gradient-to-r from-blue-900 to-indigo-800 text-white p-10 relative">
        <img 
          src="https://via.placeholder.com/400" 
          alt="Displayed Content" 
          className="rounded-lg shadow-lg" 
        />
      </div>
    </div>
  );
};

export default Login;
