import React, { useState } from 'react';
import axios from 'axios';

const PnLReport = () => {
  const [orderId, setOrderId] = useState('');
  const [pnlData, setPnlData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchPnLReport = async () => {
    if (!orderId) {
      setError("Order ID is required.");
      return;
    }

    setLoading(true);
    setError('');
    try {
      const response = await axios.get(`http://localhost:5000/api/pnl_report?orderId=${orderId}`);
      setPnlData(response.data);
    } catch (err) {
      setError("Error fetching data from the backend.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Binance Order PnL Report</h1>
      <input 
        type="text" 
        placeholder="Enter Order ID" 
        value={orderId} 
        onChange={(e) => setOrderId(e.target.value)} 
      />
      <button onClick={fetchPnLReport} disabled={loading}>
        {loading ? 'Loading...' : 'Get PnL Report'}
      </button>

      {error && <div style={{ color: 'red' }}>{error}</div>}

      {pnlData && (
        <div>
          <h2>Order PnL Details</h2>
          <p>Order ID: {pnlData.orderId}</p>
          <p>Symbol: {pnlData.symbol}</p>
          <p>Buy Price: {pnlData.buyPrice}</p>
          <p>Current Price: {pnlData.currentPrice}</p>
          <p>Quantity: {pnlData.quantity}</p>
          <p>Realized PnL: {pnlData.realizedPnL}</p>
          <p>Unrealized PnL: {pnlData.unrealizedPnL}</p>
          <p>PnL Percentage: {pnlData.pnlPercentage}%</p>
          <p>profit: {pnlData.profit}</p>
          <p>loss: {pnlData.loss}</p>
          <p>sellStatus: {pnlData.sellStatus}</p>

        </div>
      )}
    </div>
  );
};

export default PnLReport;
