import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const PnLReport = () => {
  const navigate = useNavigate();
  const [pnlData, setPnlData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Fetch all active orders periodically
  const fetchPnLReport = async () => {
    setLoading(true);
    setError("");
    try {
      const response = await axios.get("http://localhost:5000/api/pnl_report");
      console.log(response.data); // Log the response to check its structure
      setPnlData(response.data); // Assuming API returns an array of active orders
    } catch (err) {
      setError("Error fetching PnL report. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPnLReport(); // Fetch on mount
    const interval = setInterval(fetchPnLReport, 20000); // Auto-refresh every 20 seconds
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="bg-gray-800 p-6 rounded-lg shadow-lg text-white max-w-6xl mx-auto">
      <h1 className="text-2xl font-bold mb-4 text-center">Binance Active Orders PnL Report</h1>

      <button
        onClick={fetchPnLReport}
        disabled={loading}
        className="p-2 bg-yellow-500 text-black font-bold rounded hover:bg-yellow-600 w-full mb-4"
      >
        {loading ? "Refreshing..." : "Refresh PnL Report"}
      </button>

      <button
        onClick={() => navigate("/")}
        className="w-full p-2 mb-3 bg-gray-500 text-white rounded hover:bg-gray-600"
      >
        Back to Dashboard
      </button>

      {error && <div className="p-2 bg-red-600 text-white rounded mb-4">{error}</div>}

      {pnlData.length > 0 ? (
        <div className="overflow-x-auto">
          <table className="w-full text-left border-collapse border border-gray-600">
            <thead>
              <tr className="bg-gray-700 text-yellow-400">
                {["Order ID", "Symbol", "Buy Price", "Current Price", "Quantity", "Realized PnL", "Unrealized PnL", "PnL %", "Profit", "Loss", "Sell Status"].map((header, index) => (
                  <th key={index} className="border border-gray-600 p-2">{header}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {pnlData.map((order, index) => (
                <tr key={index} className="bg-gray-800 text-white">
                  <td className="border border-gray-600 p-2">{order.orderId}</td>
                  <td className="border border-gray-600 p-2">{order.symbol}</td>
                  <td className="border border-gray-600 p-2">{order.buyPrice}</td>
                  <td className="border border-gray-600 p-2">{order.currentPrice}</td>
                  <td className="border border-gray-600 p-2">{order.quantity}</td>
                  <td className="border border-gray-600 p-2">{order.realizedPnL}</td>
                  <td className="border border-gray-600 p-2">{order.unrealizedPnL}</td>
                  <td className="border border-gray-600 p-2">{order.pnlPercentage}%</td>
                  <td className="border border-gray-600 p-2">{order.profit}</td>
                  <td className="border border-gray-600 p-2">{order.loss}</td>
                  <td className="border border-gray-600 p-2">{order.sellStatus}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="text-center text-gray-400">No active orders found.</p>
      )}
    </div>
  );
};

export default PnLReport;
