import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const PlaceOrder = () => {
  const navigate = useNavigate();
  const [symbol, setSymbol] = useState("12345");
  const [side, setSide] = useState("BUY");
  const [orderType, setOrderType] = useState("LIMIT");
  const [quantity, setQuantity] = useState(1);
  const [price, setPrice] = useState("");
  const [triggerPrice, setTriggerPrice] = useState("");
  const [productType, setProductType] = useState("INTRA");
  const [timeInForce, setTimeInForce] = useState("GTC");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState(null);
  const [orders, setOrders] = useState([]);
  const [showPopup, setShowPopup] = useState(false);

  useEffect(() => {
    console.log("Updated Orders State:", orders);
  }, [orders]);

  const handlePlaceOrder = async () => {
    setError(null);
    setSuccessMessage(null);

    if (quantity <= 0) {
      setError("Quantity must be greater than 0.");
      return;
    }
    if (["LIMIT", "STOP_LOSS"].includes(orderType) && price <= 0) {
      setError("Price must be greater than 0 for LIMIT and STOP-LOSS orders.");
      return;
    }
    if (["STOP_LOSS", "STOP_LOSS_MARKET"].includes(orderType) && triggerPrice <= 0) {
      setError("Trigger price must be greater than 0 for STOP-LOSS orders.");
      return;
    }

    setLoading(true);

    try {
      const orderData = {
        security_id: symbol,
        exchange_segment: "NSE",
        transaction_type: side,
        quantity: quantity,
        order_type: orderType,
        price: ["LIMIT", "STOP_LOSS"].includes(orderType) ? price : undefined,
        trigger_price: ["STOP_LOSS", "STOP_LOSS_MARKET"].includes(orderType) ? triggerPrice : undefined,
        product_type: productType,
        timeInForce: orderType === "LIMIT" ? timeInForce : undefined,
      };

      const response = await axios.post("http://localhost:5000/api/place-order", orderData, {
        headers: {
          "Content-Type": "application/json",
        },
      });

      console.log("Order Response:", response.data);

      const newOrder = {
        orderId: response.data.orderId || "N/A",
        security_id: response.data.security_id || symbol,
        side: response.data.transaction_type || side,
        order_type: response.data.order_type || orderType,
        origQty: response.data.quantity || quantity,
        price: response.data.price || "-",
        product_type: response.data.product_type || productType,
        status: response.data.status || "Pending",
      };

      setOrders((prevOrders) => [...prevOrders, newOrder]);
      setShowPopup(true);
      setTimeout(() => setShowPopup(false), 3000);

      if (response.data.status === "offline") {
        setSuccessMessage("Market is closed. Order has been saved for later execution.");
      } else if (response.data.status === "success") {
        setSuccessMessage("Order placed successfully.");
      } else {
        setError("Failed to place order. Please try again.");
      }
    } catch (err) {
      setError(`Error placing order: ${err.message}`);
      console.error("Order Error:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-gray-800 p-6 rounded-lg shadow-lg text-white max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Place Order</h2>
      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="block mb-1">Security ID:</label>
          <input
            type="text"
            value={symbol}
            onChange={(e) => setSymbol(e.target.value)}
            className="w-full p-2 rounded bg-gray-700 border border-gray-600"
          />
        </div>
        <div>
          <label className="block mb-1">Side:</label>
          <select
            value={side}
            onChange={(e) => setSide(e.target.value)}
            className="w-full p-2 rounded bg-gray-700 border border-gray-600"
          >
            <option value="BUY">BUY</option>
            <option value="SELL">SELL</option>
          </select>
        </div>
      </div>

      <button
        onClick={handlePlaceOrder}
        disabled={loading}
        className="w-full p-2 mt-4 bg-green-500 text-black font-bold rounded hover:bg-green-600"
      >
        {loading ? "Placing Order..." : "Place Order"}
      </button>
      <button
        onClick={() => navigate("/")}
        className="w-full p-2 mt-3 bg-gray-500 text-white rounded hover:bg-gray-600"
      >
        Back to Dashboard
      </button>

      {error && <p className="mt-4 p-2 bg-red-400 text-black rounded">{error}</p>}

      {orders.length > 0 && (
        <div className="mt-6">
          <h3 className="text-xl font-bold mb-2">Order History</h3>
          <div className="overflow-x-auto">
            <table className="w-full border-collapse border border-gray-600">
              <thead>
                <tr className="bg-gray-700 text-white">
                  <th className="border border-gray-600 p-2">Order ID</th>
                  <th className="border border-gray-600 p-2">Security ID</th>
                  <th className="border border-gray-600 p-2">Side</th>
                  <th className="border border-gray-600 p-2">Order Type</th>
                  <th className="border border-gray-600 p-2">Quantity</th>
                  <th className="border border-gray-600 p-2">Price</th>
                  <th className="border border-gray-600 p-2">Product Type</th>
                  <th className="border border-gray-600 p-2">Status</th>
                </tr>
              </thead>
              <tbody>
                {orders.map((order, index) => (
                  <tr key={index} className="bg-gray-800 text-white">
                    <td className="border border-gray-600 p-2">{order.orderId}</td>
                    <td className="border border-gray-600 p-2">{order.security_id}</td>
                    <td className="border border-gray-600 p-2">{order.side}</td>
                    <td className="border border-gray-600 p-2">{order.order_type}</td>
                    <td className="border border-gray-600 p-2">{order.origQty}</td>
                    <td className="border border-gray-600 p-2">{order.price || "-"}</td>
                    <td className="border border-gray-600 p-2">{order.product_type}</td>
                    <td className="border border-gray-600 p-2">{order.status}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {successMessage && <p className="mt-4 p-2 bg-green-600 text-white rounded">{successMessage}</p>}
      {showPopup && (
        <div className="fixed top-10 right-10 bg-green-500 text-white p-3 rounded-lg shadow-lg">
          Order created successfully!
        </div>
      )}
    </div>
  );
};

export default PlaceOrder;
