import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { io } from "socket.io-client";
import { FaSpinner } from "react-icons/fa";

const SOCKET_URL = "http://localhost:5000";
const API_URL = "http://localhost:5000/api/pnl_report";
const SELL_API_URL = "http://localhost:5000/api/sell_order";
const SELL_ALL_API_URL = "http://localhost:5000/api/sell_all_orders";

const PnLReport = () => {
  const navigate = useNavigate();
  const [pnlData, setPnlData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [sellingOrderId, setSellingOrderId] = useState(null);
  const [sellingAll, setSellingAll] = useState(false);
  const socketRef = useRef(null);

  const fetchPnLReport = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(API_URL);
      if (Array.isArray(response.data)) {
        setPnlData(response.data.filter(order => order));
      } else if (typeof response.data === "object" && response.data !== null) {
        setPnlData([response.data]);
      } else {
        setError("Invalid data format received from API.");
        setPnlData([]);
      }
      setShowPopup(true);
      setTimeout(() => setShowPopup(false), 3000);
    } catch (err) {
      setError("Failed to fetch PnL report. Please try again.");
      setPnlData([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPnLReport();
    if (!socketRef.current) {
      socketRef.current = io(SOCKET_URL);
      socketRef.current.on("price_update", (data) => {
        if (data && typeof data === "object" && Object.keys(data).length > 0) {
          const symbol = Object.keys(data)[0];
          const newPrice = Object.values(data)[0];
          setPnlData((prevData) =>
            prevData.map((order) =>
              order.symbol === symbol
                ? {
                    ...order,
                    currentPrice: newPrice,
                    unrealizedPnL: (newPrice - order.buyPrice) * order.quantity,
                    pnlPercentage: (((newPrice - order.buyPrice) * 100) / order.buyPrice).toFixed(2),
                  }
                : order
            )
          );
        }
      });
    }
    return () => {
      socketRef.current?.disconnect();
      socketRef.current = null;
    };
  }, []);

  const handleSellOrder = async (symbol, orderId) => {
    setSellingOrderId(orderId);
    setError(null);
    try {
      await axios.post(SELL_API_URL, { symbol, order_id: orderId });
      fetchPnLReport();
    } catch (error) {
      setError("Failed to sell order.");
    } finally {
      setSellingOrderId(null);
    }
  };

  const handleSellAllOrders = async () => {
    setSellingAll(true);
    setError(null);
    try {
      await axios.post(SELL_ALL_API_URL);
      fetchPnLReport();
    } catch (error) {
      setError("Failed to sell all orders.");
    } finally {
      setSellingAll(false);
    }
  };

  return (
    <div className="bg-gray-800 p-6 rounded-lg shadow-lg text-white max-w-6xl mx-auto">
      <h2 className="text-2xl font-bold mb-4 text-center">Binance Active Orders PnL Report</h2>
      <button
        onClick={fetchPnLReport}
        disabled={loading}
        className="w-full p-2 mb-4 bg-yellow-500 text-black font-bold rounded hover:bg-yellow-600 flex items-center justify-center"
      >
        {loading ? <FaSpinner className="animate-spin mr-2" /> : "Refresh PnL Report"}
      </button>
      <button onClick={() => navigate("/")} className="w-full p-2 mb-3 bg-gray-500 text-white rounded hover:bg-gray-600">Back to Dashboard</button>
      {error && <p className="mt-4 p-2 bg-red-600 text-white rounded">{error}</p>}
      {pnlData.length > 0 ? (
        <table className="min-w-full border border-gray-600 shadow-lg text-center">
          <thead>
            <tr className="bg-gray-700 text-yellow-400">
              {["Timestamp", "Order ID", "Symbol", "Buy Price ($)", "Current Price ($)", "Quantity", "Unrealized PnL ($)", "PnL %", "Sell Order"].map((header, index) => (
                <th key={index} className="border border-gray-600 p-3 text-sm">{header}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {pnlData.map((order, index) => (
              <tr key={index} className="bg-gray-900 hover:bg-gray-700 transition">
                <td className="border border-gray-600 p-2 text-xs">{order.timestamp || "N/A"}</td>
                <td className="border border-gray-600 p-2 text-xs">{order.orderId || "N/A"}</td>
                <td className="border border-gray-600 p-2 text-xs font-bold">{order.symbol || "N/A"}</td>
                <td className="border border-gray-600 p-2 text-xs text-green-400">${order.buyPrice?.toFixed(2) || "N/A"}</td>
                <td className="border border-gray-600 p-2 text-xs text-blue-400">${order.currentPrice?.toFixed(2) || "N/A"}</td>
                <td className="border border-gray-600 p-2 text-xs">{order.quantity?.toFixed(3) || "N/A"}</td>
                <td className="border border-gray-600 p-2 text-xs text-red-300">${order.unrealizedPnL?.toFixed(2) || "N/A"}</td>
                <td className="border border-gray-600 p-2 text-xs font-bold">{order.pnlPercentage || "N/A"}%</td>
                <td className="border border-gray-600 p-2">
                  <button onClick={() => handleSellOrder(order.symbol, order.orderId)} disabled={sellingOrderId === order.orderId} className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600">
                    {sellingOrderId === order.orderId ? <FaSpinner className="animate-spin" /> : "Sell"}
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p className="text-center text-gray-400">No active orders found.</p>
      )}
    </div>
  );
};

export default PnLReport;
