import React, { useState, useEffect } from "react";
import axios from "axios";

const PlaceOrder = () => {
  const [security_id, setSecurityId] = useState("12345");
  const [exchange_segment, setExchangeSegment] = useState("NSE");
  const [side, setSide] = useState("BUY");
  const [orderType, setOrderType] = useState("LIMIT");
  const [quantity, setQuantity] = useState(1);
  const [price, setPrice] = useState("");
  const [triggerPrice, setTriggerPrice] = useState("");
  const [productType, setProductType] = useState("INTRA");
  const [timeInForce, setTimeInForce] = useState("GTC");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState(null);
  const [orders, setOrders] = useState([]);

  // Fetch existing orders on component mount
  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/orders");
        setOrders(response.data);
      } catch (error) {
        console.error("Error fetching orders:", error);
      }
    };
    fetchOrders();
  }, []);

  const handlePlaceOrder = async () => {
    setError(null);
    setSuccessMessage(null);

    if (quantity <= 0) {
      setError("Quantity must be greater than 0.");
      return;
    }
    if (["LIMIT", "STOP_LOSS"].includes(orderType) && (!price || price <= 0)) {
      setError("Price must be greater than 0 for LIMIT and STOP-LOSS orders.");
      return;
    }
    if (["STOP_LOSS", "STOP_LOSS_MARKET"].includes(orderType) && (!triggerPrice || triggerPrice <= 0)) {
      setError("Trigger price must be greater than 0 for STOP-LOSS orders.");
      return;
    }

    setLoading(true);

    try {
      const orderData = {
        security_id,
        exchange_segment,
        transaction_type: side,
        quantity,
        order_type: orderType,
        price: ["LIMIT", "STOP_LOSS"].includes(orderType) ? price : undefined,
        trigger_price: ["STOP_LOSS", "STOP_LOSS_MARKET"].includes(orderType) ? triggerPrice : undefined,
        product_type: productType,
        timeInForce: orderType === "LIMIT" ? timeInForce : undefined,
      };

      const response = await axios.post("http://localhost:5000/api/place-order", orderData, {
        headers: { "Content-Type": "application/json" },
      });

      console.log("Order Response:", response.data);
      setOrders((prevOrders) => [...prevOrders, response.data]);
      setSuccessMessage("Order placed successfully.");
    } catch (err) {
      setError(`Error placing order: ${err.message}`);
      console.error("Order Error:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-gray-800 p-6 rounded-lg shadow-lg text-white max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Place Order</h2>
      <button onClick={handlePlaceOrder} disabled={loading} className="w-full p-2 mt-4 bg-green-500 text-black font-bold rounded hover:bg-green-600">
        {loading ? "Placing Order..." : "Place Order"}
      </button>
      {error && <p className="mt-4 p-2 bg-red-400 text-black rounded">{error}</p>}
      {successMessage && <p className="mt-4 p-2 bg-green-600 text-white rounded">{successMessage}</p>}

      {orders.length > 0 && (
        <div className="mt-6">
          <h3 className="text-xl font-bold mb-2">Order History</h3>
          <table className="w-full border-collapse border border-gray-600">
            <thead>
              <tr className="bg-gray-700 text-white">
                <th className="p-2 border border-gray-600">Security ID</th>
                <th className="p-2 border border-gray-600">Exchange</th>
                <th className="p-2 border border-gray-600">Type</th>
                <th className="p-2 border border-gray-600">Qty</th>
                <th className="p-2 border border-gray-600">Price</th>
                <th className="p-2 border border-gray-600">Status</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((order, index) => (
                <tr key={index} className="bg-gray-800">
                  <td className="p-2 border border-gray-600">{order.security_id}</td>
                  <td className="p-2 border border-gray-600">{order.exchange_segment}</td>
                  <td className="p-2 border border-gray-600">{order.transaction_type}</td>
                  <td className="p-2 border border-gray-600">{order.quantity}</td>
                  <td className="p-2 border border-gray-600">{order.price || "-"}</td>
                  <td className="p-2 border border-gray-600">{order.status || "Pending"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default PlaceOrder;
