import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { io } from "socket.io-client";
import { FaSpinner } from "react-icons/fa"; // Loading Spinner

const SOCKET_URL = "http://localhost:5000"; // Flask WebSocket Server
const API_URL = "http://localhost:5000/api/pnl_report";
const SELL_API_URL = "http://localhost:5000/api/sell_order"; // Endpoint to sell one order
const SELL_ALL_API_URL = "http://localhost:5000/api/sell_all_orders"; // Endpoint to sell all orders

const PnLReport = () => {
  const navigate = useNavigate();
  const [pnlData, setPnlData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [selling, setSelling] = useState(false); // Track sell button state
  const socketRef = useRef(null);

  // Fetch initial PnL report data
  const fetchPnLReport = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(API_URL);
      console.log("API Response:", response.data);

      if (Array.isArray(response.data)) {
        setPnlData(response.data.filter(order => order));
      } else if (typeof response.data === "object" && response.data !== null) {
        setPnlData([response.data]);
      } else {
        setError("Invalid data format received from API.");
        setPnlData([]);
      }

      setShowPopup(true);
      setTimeout(() => setShowPopup(false), 3000);
    } catch (err) {
      setError("Failed to fetch PnL report. Please try again.");
      setPnlData([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPnLReport();

    // Establish WebSocket connection
    if (!socketRef.current) {
      socketRef.current = io(SOCKET_URL);

      socketRef.current.on("price_update", (data) => {
        console.log("WebSocket Price Update:", data);

        if (data && typeof data === "object" && Object.keys(data).length > 0) {
          const symbol = Object.keys(data)[0];
          const newPrice = Object.values(data)[0];

          setPnlData((prevData) =>
            prevData.map((order) => {
              if (order.symbol === symbol) {
                const updatedCurrentPrice = newPrice;
                const unrealizedPnL = (updatedCurrentPrice - order.buyPrice) * order.quantity;
                const pnlPercentage = ((unrealizedPnL / (order.buyPrice * order.quantity)) * 100).toFixed(2);
                const realizedPnL = order.realizedPnL || 0;
                const profit = unrealizedPnL > 0 ? unrealizedPnL : 0;
                const loss = unrealizedPnL < 0 ? Math.abs(unrealizedPnL) : 0;

                return {
                  ...order,
                  currentPrice: updatedCurrentPrice,
                  unrealizedPnL,
                  pnlPercentage,
                  profit,
                  loss,
                  realizedPnL,
                };
              }
              return order;
            })
          );
        }
      });

      socketRef.current.on("connect_error", (err) => {
        console.error("WebSocket Connection Error:", err);
      });

      socketRef.current.on("disconnect", () => {
        console.warn("WebSocket Disconnected");
      });
    }

    return () => {
      socketRef.current?.disconnect();
      socketRef.current = null;
    };
  }, []);

  // Sell a single order
  const handleSellOrder = async (orderId) => {
    try {
      setSelling(true);
      await axios.post(SELL_API_URL, { order_id: orderId });
      fetchPnLReport(); // Refresh PnL after selling
    } catch (error) {
      console.error("Error selling order:", error);
      setError("Failed to sell order.");
    } finally {
      setSelling(false);
    }
  };

  // Sell all orders
  const handleSellAllOrders = async () => {
    try {
      setSelling(true);
      await axios.post(SELL_ALL_API_URL);
      fetchPnLReport(); // Refresh PnL after selling all
    } catch (error) {
      console.error("Error selling all orders:", error);
      setError("Failed to sell all orders.");
    } finally {
      setSelling(false);
    }
  };

  return (
    <div className="bg-gray-800 p-6 rounded-lg shadow-lg text-white max-w-6xl mx-auto">
      <h2 className="text-2xl font-bold mb-4 text-center">
        Binance Active Orders PnL Report
      </h2>

      {/* Refresh Button */}
      <button
        onClick={fetchPnLReport}
        disabled={loading}
        className="w-full p-2 mb-4 bg-yellow-500 text-black font-bold rounded hover:bg-yellow-600 flex items-center justify-center"
      >
        {loading ? <FaSpinner className="animate-spin mr-2" /> : "Refresh PnL Report"}
      </button>

      

      {/* Back Button */}
      <button
        onClick={() => navigate("/")}
        className="w-full p-2 mb-3 bg-gray-500 text-white rounded hover:bg-gray-600"
      >
        Back to Dashboard
      </button>

      {/* Error Message */}
      {error && <p className="mt-4 p-2 bg-red-600 text-white rounded">{error}</p>}

      {/* Table with PnL Data */}
      {pnlData.length > 0 ? (
        <div className="overflow-x-auto">
          <table className="min-w-full border border-gray-600 shadow-lg text-center">
            <thead>
              <tr className="bg-gray-700 text-yellow-400">
              {["Timestamp", "Order ID", "Symbol", "Buy Price ($)", "Current Price ($)", "Quantity", "Realized PnL ($)", "Unrealized PnL ($)", "PnL %", "Profit ($)", "Loss ($)", "Sell Status"].map((header, index) => (
                  <th key={index} className="border border-gray-600 p-3 text-sm">{header}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {pnlData.map((order, index) => (
                <tr key={index} className="bg-gray-900 hover:bg-gray-700 transition">
                  <td className="border border-gray-600 p-2 text-xs">{order.timestamp || "N/A"}</td>
                  <td className="border border-gray-600 p-2 text-xs">{order.orderId || "N/A"}</td>
                  <td className="border border-gray-600 p-2 text-xs font-bold">{order.symbol || "N/A"}</td>
                  <td className="border border-gray-600 p-2 text-xs text-green-400">${order.buyPrice?.toFixed(2) || "N/A"}</td>
                  <td className="border border-gray-600 p-2 text-xs text-blue-400">${order.currentPrice?.toFixed(2) || "N/A"}</td>
                  <td className="border border-gray-600 p-2 text-xs">{order.quantity?.toFixed(4) || "N/A"}</td>
                  <td className="border border-gray-600 p-2 text-xs text-green-300">${order.realizedPnL?.toFixed(2) || "N/A"}</td>
                  <td className="border border-gray-600 p-2 text-xs text-red-300">${order.unrealizedPnL?.toFixed(2) || "N/A"}</td>
                  <td className="border border-gray-600 p-2 text-xs font-bold">{order.pnlPercentage?.toFixed(2) || "N/A"}%</td>
                  <td className="border border-gray-600 p-2 text-xs text-green-500">${order.profit?.toFixed(2) || "N/A"}</td>
                  <td className="border border-gray-600 p-2 text-xs text-red-500">${order.loss?.toFixed(2) || "N/A"}</td>
                  <td className="border border-gray-600 p-2 text-xs font-bold">{order.sellStatus || "N/A"}</td>

                  <td className="border border-gray-600 p-2">
                    <button
                      onClick={() => handleSellOrder(order.orderId)}
                      className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-700"
                      disabled={selling}
                    >
                      Sell
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
            <tfoot>
                <tr className="bg-gray-700 font-bold text-white">
                 <td colSpan="6" className="border border-gray-600 p-2 text-center">Total</td>
                  <td className="border border-gray-600 p-2 text-green-300">
                   ${pnlData.reduce((sum, order) => sum + (order.realizedPnL || 0), 0).toFixed(2)}
                  </td>
                  <td className="border border-gray-600 p-2 text-red-300">
                    ${pnlData.reduce((sum, order) => sum + (order.unrealizedPnL || 0), 0).toFixed(2)}
                  </td>
                  <td className="border border-gray-600 p-2 text-yellow-300">
                    {pnlData.length > 0
                      ? (pnlData.reduce((sum, order) => sum + (parseFloat(order.pnlPercentage) || 0), 0) / pnlData.length).toFixed(2) + "%"
                      : "N/A"}
                   </td>
                   <td className="border border-gray-600 p-2 text-green-500">
                     ${pnlData.reduce((sum, order) => sum + (order.profit || 0), 0).toFixed(2)}
                   </td>
                   <td className="border border-gray-600 p-2 text-red-500">
                     ${pnlData.reduce((sum, order) => sum + (order.loss || 0), 0).toFixed(2)}
                   </td>
                   <td className="border border-gray-600 p-2">
                    {/* Sell All Orders Button */}
                  <button
                    onClick={handleSellAllOrders}
                    disabled={selling}
                    className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600"
                  >
                    {selling ? <FaSpinner className="animate-spin mr-2" /> : "Sell All Orders"}
                  </button>
                  </td> 
                 </tr>
                </tfoot>
          </table>
        </div>
      ) : (
        <p className="text-center text-gray-400">No active orders found.</p>
      )}
      {/* Success Popup */}
     {showPopup && (
        <div className="fixed top-10 right-10 bg-green-500 text-white p-3 rounded-lg shadow-lg">
          Data fetched successfully!
        </div>
      )}
    </div>
    
  );
};

export default PnLReport;
