import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const PlaceOrder = () => {
  const navigate = useNavigate();
  const [symbol, setSymbol] = useState("12345");
  const [side, setSide] = useState("BUY");
  const [orderType, setOrderType] = useState("LIMIT");
  const [quantity, setQuantity] = useState(0.001);
  const [price, setPrice] = useState("");
  const [timeInForce, setTimeInForce] = useState("GTC");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [successMessage, setSuccessMessage] = useState(null);
  const [orders, setOrders] = useState([]);
  const [showPopup, setShowPopup] = useState(false);


  const handlePlaceOrder = async () => {
    if (quantity <= 0) {
      setError("Quantity must be greater than 0.");
      return;
    }
    if (orderType === "LIMIT" && price <= 0) {
      setError("Price must be greater than 0 for LIMIT orders.");
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const orderData = {
        security_id: symbol,
        side: side,
        type: orderType,
        quantity: quantity,
        price: orderType === "LIMIT" ? price : undefined,
        timeInForce: orderType === "LIMIT" ? timeInForce : undefined,
      };

      const response = await axios.post("http://localhost:5000/place-order", orderData);
      setOrders((prevOrders) => [...prevOrders, response.data]);
      setShowPopup(true);
      setTimeout(() => setShowPopup(false), 3000);
      if (response.data.status === "offline") {
        setSuccessMessage("Market is closed. Order has been saved for later execution.");
      } else if (response.data.status === "success") {
        setSuccessMessage("Order placed successfully.");
      } else {
        setError("Failed to place order. Please try again.");
      }
    } catch (err) {
      setError("Error placing order. Please try again.",err);
      console.log("====================",err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-gray-800 p-6 rounded-lg shadow-lg text-white max-w-3xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Place Order</h2>
      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="block mb-1">security_id:</label>
          <input
            type="text"
            value={symbol}
            onChange={(e) => setSymbol(e.target.value)}
            className="w-full p-2 rounded bg-gray-700 border border-gray-600"
          />
        </div>
        <div>
          <label className="block mb-1">Side:</label>
          <select
            value={side}
            onChange={(e) => setSide(e.target.value)}
            className="w-full p-2 rounded bg-gray-700 border border-gray-600"
          >
            <option value="BUY">BUY</option>
            <option value="SELL">SELL</option>
          </select>
        </div>
        <div>
          <label className="block mb-1">Order Type:</label>
          <select
            value={orderType}
            onChange={(e) => setOrderType(e.target.value)}
            className="w-full p-2 rounded bg-gray-700 border border-gray-600"
          >
            <option value="LIMIT">LIMIT</option>
            <option value="MARKET">MARKET</option>
          </select>
        </div>
        {orderType === "LIMIT" && (
          <>
            {/* <div>
              <label className="block mb-1">Price:</label>
              <input
                type="number"
                value={price}
                onChange={(e) => setPrice(parseFloat(e.target.value) || "")}
                className="w-full p-2 rounded bg-gray-700 border border-gray-600"
              />
            </div> */}
            <div>
              <label className="block mb-1">Time In Force:</label>
              <select
                value={timeInForce}
                onChange={(e) => setTimeInForce(e.target.value)}
                className="w-full p-2 rounded bg-gray-700 border border-gray-600"
              >
                <option value="GTC">GTC (Good Till Cancelled)</option>
                <option value="IOC">IOC (Immediate Or Cancel)</option>
                <option value="FOK">FOK (Fill Or Kill)</option>
              </select>
            </div>
          </>
        )}
        <div>
          <label className="block mb-1">Quantity:</label>
          <input
            type="number"
            value={quantity}
            onChange={(e) => setQuantity(parseFloat(e.target.value) || 0)}
            className="w-full p-2 rounded bg-gray-700 border border-gray-600"
          />
        </div>
        <div>
              <label className="block mb-1">Price:</label>
              <input
                type="number"
                value={price}
                onChange={(e) => setPrice(parseFloat(e.target.value) || 0)}
                className="w-full p-2 rounded bg-gray-700 border border-gray-600"
              />
            </div>
      </div>
      <button
        onClick={handlePlaceOrder}
        disabled={loading}
        className="w-full p-2 mt-4 bg-green-500 text-black font-bold rounded hover:bg-green-600"
      >
        {loading ? "Placing Order..." : "Place Order"}
      </button>
      <button
        onClick={() => navigate("/")}
        className="w-full p-2 mt-3 bg-gray-500 text-white rounded hover:bg-gray-600"
      >
        Back to Dashboard
      </button>

      {error && <p className="mt-4 p-2 bg-red-600 text-white rounded">{error}</p>}
      {/* Order Table */}
       {orders.length > 0 && (
         <div className="mt-6">
           <h3 className="text-xl font-bold mb-2">Order History</h3>
           <div className="overflow-x-auto">
             <table className="w-full border-collapse border border-gray-600">
                <thead>
                  <tr className="bg-gray-700 text-white">
                    <th className="border border-gray-600 p-2">Order ID</th>
                    <th className="border border-gray-600 p-2">Symbol</th>
                    <th className="border border-gray-600 p-2">Side</th>
                    <th className="border border-gray-600 p-2">Type</th>
                   <th className="border border-gray-600 p-2">Quantity</th>
                   <th className="border border-gray-600 p-2">Price</th>
                   <th className="border border-gray-600 p-2">Status</th>
                 </tr>
               </thead>
               <tbody>
                  {orders.map((order, index) => (
                    <tr key={index} className="bg-gray-800 text-white">
                     <td className="border border-gray-600 p-2">{order.orderId}</td>
                     <td className="border border-gray-600 p-2">{order.symbol}</td>
                     <td className="border border-gray-600 p-2">{order.side}</td>
                     <td className="border border-gray-600 p-2">{order.type}</td>
                     <td className="border border-gray-600 p-2">{order.origQty}</td>
                     <td className="border border-gray-600 p-2">{order.price || "-"}</td>
                     <td className="border border-gray-600 p-2">{order.status}</td>
                   </tr>
                 ))}
              </tbody>
              </table>
           </div>
         </div>
       )}
       {showPopup && (
         <div className="fixed top-10 right-10 bg-green-500 text-white p-3 rounded-lg shadow-lg">
           Order created successfully!
         </div>
       )}
      {successMessage && <p className="mt-4 p-2 bg-green-600 text-white rounded">{successMessage}</p>}
    </div>
  );
};

export default PlaceOrder;
