import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { io } from "socket.io-client";
import { FaSpinner } from "react-icons/fa"; // For loading spinner

const SOCKET_URL = "http://localhost:5000"; // Flask WebSocket Server
const API_URL = "http://localhost:5000/api/pnl_report";

const PnLReport = () => {
  const navigate = useNavigate();
  const [pnlData, setPnlData] = useState(() => []);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const socketRef = useRef(null); // Persistent WebSocket instance

  // Fetch initial PnL report data
  const fetchPnLReport = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(API_URL);
      console.log("API Response:", response.data);

      if (Array.isArray(response.data)) {
        // setPnlData(response.data);
        setPnlData(response.data.filter(order => order));
      } else if (typeof response.data === "object") {
        setPnlData([response.data]);
      } else {
        setError("Invalid data format received from API.");
        setPnlData([]);
      }

      setShowPopup(true);
      setTimeout(() => setShowPopup(false), 3000);
    } catch (err) {
      setError("Failed to fetch PnL report. Please try again.");
      setPnlData([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPnLReport();

    // Establish WebSocket connection (only once)
    if (!socketRef.current) {
      socketRef.current = io(SOCKET_URL);

      socketRef.current.on("price_update", (data) => {
        console.log("WebSocket Price Update:", data);
        if (data && typeof data === "object" && Object.keys(data).length > 0) {
          const symbol = Object.keys(data)[0];
          const newPrice = Object.values(data)[0];

          setPnlData((prevData) =>
            prevData.map((order) =>
              order.symbol === symbol ? { ...order, currentPrice: newPrice } : order
            )
          );
        } else {
          console.warn("Received invalid WebSocket data:", data);
        }
      });

      socketRef.current.on("connect_error", (err) => {
        console.error("WebSocket Connection Error:", err);
      });

      socketRef.current.on("disconnect", () => {
        console.warn("WebSocket Disconnected");
      });
    }

    return () => {
      socketRef.current?.disconnect();
      socketRef.current = null;
    };
  }, []);

  return (
    <div className="bg-gray-800 p-6 rounded-lg shadow-lg text-white max-w-6xl mx-auto">
      <h2 className="text-2xl font-bold mb-4 text-center">
        Binance Active Orders PnL Report
      </h2>

      {/* Refresh Button */}
      <button
        onClick={fetchPnLReport}
        disabled={loading}
        className="w-full p-2 mb-4 bg-yellow-500 text-black font-bold rounded hover:bg-yellow-600 flex items-center justify-center"
      >
        {loading ? <FaSpinner className="animate-spin mr-2" /> : "Refresh PnL Report"}
      </button>

      {/* Back Button */}
      <button
        onClick={() => navigate("/")}
        className="w-full p-2 mb-3 bg-gray-500 text-white rounded hover:bg-gray-600"
      >
        Back to Dashboard
      </button>

      {/* Error Message */}
      {error && <p className="mt-4 p-2 bg-red-600 text-white rounded">{error}</p>}

      {/* Table with PnL Data */}
      {pnlData.length > 0 ? (
        <div className="overflow-x-auto">
          <table className="min-w-full border border-gray-600 shadow-lg text-center">
            <thead>
              <tr className="bg-gray-700 text-yellow-400">
                {[
                  "Timestamp",
                  "Order ID",
                  "Symbol",
                  "Buy Price ($)",
                  "Current Price ($)",
                  "Quantity",
                  "Realized PnL ($)",
                  "Unrealized PnL ($)",
                  "PnL %",
                  "Profit ($)",
                  "Loss ($)",
                  "Sell Status",
                  // "Response",
                ].map((header, index) => (
                  <th key={index} className="border border-gray-600 p-3 text-sm">{header}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {pnlData.map((order, index) => (
                <tr key={index} className="bg-gray-900 hover:bg-gray-700 transition">
                  <td className="border border-gray-600 p-2 text-xs">{order.timestamp || "N/A"}</td>
                  <td className="border border-gray-600 p-2 text-xs">{order.orderId || "N/A"}</td>
                  <td className="border border-gray-600 p-2 text-xs font-bold">{order.symbol || "N/A"}</td>
                  <td className="border border-gray-600 p-2 text-xs text-green-400">
                    {order.buyPrice ? `$${order.buyPrice.toFixed(2)}` : "N/A"}
                  </td>
                  <td className="border border-gray-600 p-2 text-xs text-blue-400">
                    {order.currentPrice ? `$${order.currentPrice.toFixed(2)}` : "N/A"}
                  </td>
                  <td className="border border-gray-600 p-2 text-xs">{order.quantity || "N/A"}</td>
                  <td className="border border-gray-600 p-2 text-xs text-green-300">
                    {order.realizedPnL ? `$${order.realizedPnL.toFixed(2)}` : "N/A"}
                  </td>
                  <td className="border border-gray-600 p-2 text-xs text-red-300">
                    {order.unrealizedPnL ? `$${order.unrealizedPnL.toFixed(2)}` : "N/A"}
                  </td>
                  <td className="border border-gray-600 p-2 text-xs font-bold">
                    {order.pnlPercentage !== undefined ? `${order.pnlPercentage.toFixed(2)}%` : "N/A"}
                  </td>
                  <td className="border border-gray-600 p-2 text-xs text-green-500">
                    {order.profit ? `$${order.profit.toFixed(2)}` : "N/A"}
                  </td>
                  <td className="border border-gray-600 p-2 text-xs text-red-500">
                    {order.loss ? `$${order.loss.toFixed(2)}` : "N/A"}
                  </td>
                  <td className="border border-gray-600 p-2 text-xs font-bold">{order.sellStatus || "N/A"}</td>
                  {/* <td className="border border-gray-600 p-2 text-xs">{order.response || "N/A"}</td> */}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p className="text-center text-gray-400">No active orders found.</p>
      )}

      {/* Success Popup */}
      {showPopup && (
        <div className="fixed top-10 right-10 bg-green-500 text-white p-3 rounded-lg shadow-lg">
          Data fetched successfully!
        </div>
      )}
    </div>
  );
};

export default PnLReport;
