import React, { useState } from "react";
// import { useNavigate } from "react-router-dom";
import axios from "axios";

const PlaceOrder = () => {
  const [symbol, setSymbol] = useState("BTCUSDT");
  const [side, setSide] = useState("BUY");
  const [orderType, setOrderType] = useState("LIMIT");
  const [quantity, setQuantity] = useState(0.001);
  const [price, setPrice] = useState(""); // Price for LIMIT orders
  const [orderMode, setOrderMode] = useState("real"); // "real" or "dummy"
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [orders, setOrders] = useState([]);

  const handlePlaceOrder = async () => {
    setLoading(true);
    setError(null);

    try {
      const orderData = {
        symbol,
        side,
        type: orderType,
        quantity,
        orderMode, // Pass mode to API
      };

      if (orderType === "LIMIT") {
        orderData.price = price;
      }

      const response = await axios.post("http://localhost:5000/place-order", orderData);
      setOrders((prevOrders) => [...prevOrders, response.data]);
    } catch (err) {
      setError("Failed to place the order.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 rounded-lg shadow-lg text-white max-w-3xl mx-auto bg-gray-800">
      <h2 className="text-2xl font-bold mb-4">Place Order</h2>

      {/* Order Mode Selection */}
      <div className="mb-4">
        <label className="mr-4">Order Mode:</label>
        <select
          value={orderMode}
          onChange={(e) => setOrderMode(e.target.value)}
          className="p-2 rounded bg-gray-700 border border-gray-600"
        >
          <option value="real">Real Order</option>
          <option value="dummy">Dummy Order</option>
        </select>
      </div>

      {/* Input Fields */}
      <input type="text" value={symbol} onChange={(e) => setSymbol(e.target.value)} placeholder="Symbol" />
      <select value={side} onChange={(e) => setSide(e.target.value)}>
        <option value="BUY">BUY</option>
        <option value="SELL">SELL</option>
      </select>
      <select value={orderType} onChange={(e) => setOrderType(e.target.value)}>
        <option value="MARKET">MARKET</option>
        <option value="LIMIT">LIMIT</option>
      </select>
      {orderType === "LIMIT" && <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} placeholder="Price" />}
      <input type="number" value={quantity} onChange={(e) => setQuantity(e.target.value)} placeholder="Quantity" />

      <button onClick={handlePlaceOrder} disabled={loading} className="bg-yellow-500 text-black p-2">
        {loading ? "Placing Order..." : "Place Order"}
      </button>

      {error && <p className="text-red-500">{error}</p>}
      {/* Order Table */}
      {orders.length > 0 && (
        <div className="mt-6">
          <h3 className="text-xl font-bold mb-2">Order History</h3>
          <div className="overflow-x-auto">
            <table className="w-full border-collapse border border-gray-600">
               <thead>
                 <tr className="bg-gray-700 text-white">
                   <th className="border border-gray-600 p-2">Order ID</th>
                   <th className="border border-gray-600 p-2">Symbol</th>
                   <th className="border border-gray-600 p-2">Side</th>
                   <th className="border border-gray-600 p-2">Type</th>
                  <th className="border border-gray-600 p-2">Quantity</th>
                  <th className="border border-gray-600 p-2">Price</th>
                  <th className="border border-gray-600 p-2">Status</th>
                </tr>
              </thead>
              <tbody>
                 {orders.map((order, index) => (
                   <tr key={index} className="bg-gray-800 text-white">
                    <td className="border border-gray-600 p-2">{order.orderId}</td>
                    <td className="border border-gray-600 p-2">{order.symbol}</td>
                    <td className="border border-gray-600 p-2">{order.side}</td>
                    <td className="border border-gray-600 p-2">{order.type}</td>
                    <td className="border border-gray-600 p-2">{order.origQty}</td>
                    <td className="border border-gray-600 p-2">{order.price || "-"}</td>
                    <td className="border border-gray-600 p-2">{order.status}</td>
                  </tr>
                ))}
             </tbody>
             </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default PlaceOrder;
