import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { io } from "socket.io-client";
import { FaSpinner } from "react-icons/fa"; // Loading Spinner

const SOCKET_URL = "http://localhost:5000"; // Flask WebSocket Server
const API_URL = "http://localhost:5000/api/pnl_report";
const SELL_API_URL = "http://localhost:5000/api/sell_order"; // Endpoint to sell one order
const SELL_ALL_API_URL = "http://localhost:5000/api/sell_all_orders"; // Endpoint to sell all orders

const PnLReport = () => {
  const navigate = useNavigate();
  const [pnlData, setPnlData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [sellingOrder, setSellingOrder] = useState(null); // Track which order is being sold
  const socketRef = useRef(null);

  // Fetch PnL report data
  const fetchPnLReport = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(API_URL);
      setPnlData(response.data || []);
    } catch (err) {
      setError("Failed to fetch PnL report.");
      setPnlData([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPnLReport();
    if (!socketRef.current) {
      socketRef.current = io(SOCKET_URL);
      socketRef.current.on("price_update", (data) => {
        console.log("WebSocket Price Update:", data);
        setPnlData((prevData) =>
          prevData.map((order) =>
            order.symbol === data.symbol
              ? { ...order, currentPrice: data.price }
              : order
          )
        );
      });
    }
    return () => {
      socketRef.current?.disconnect();
      socketRef.current = null;
    };
  }, []);

  // âœ… Handle Single Order Sell
  const handleSellOrder = async (symbol, orderId) => {
    if (!symbol || !orderId) {
      setError("Invalid order details.");
      return;
    }

    try {
      setSellingOrder(orderId); // Show loading for this order
      const response = await axios.post(SELL_API_URL, { symbol, order_id: orderId });

      alert(`Sell Order Success: ${response.data.message}`);
      fetchPnLReport(); // Refresh data after sell
    } catch (error) {
      console.error("Error selling order:", error);
      setError("Failed to sell order.");
    } finally {
      setSellingOrder(null);
    }
  };

  // âœ… Handle Sell All Orders
  const handleSellAllOrders = async () => {
    try {
      setLoading(true);
      await axios.post(SELL_ALL_API_URL);
      fetchPnLReport();
    } catch (error) {
      console.error("Error selling all orders:", error);
      setError("Failed to sell all orders.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="bg-gray-800 p-6 rounded-lg shadow-lg text-white max-w-6xl mx-auto">
      <h2 className="text-2xl font-bold mb-4 text-center">Binance Active Orders PnL Report</h2>

      {/* Refresh Button */}
      <button onClick={fetchPnLReport} disabled={loading} className="w-full p-2 mb-4 bg-yellow-500 text-black font-bold rounded hover:bg-yellow-600 flex items-center justify-center">
        {loading ? <FaSpinner className="animate-spin mr-2" /> : "Refresh PnL Report"}
      </button>

      {/* Back Button */}
      <button onClick={() => navigate("/")} className="w-full p-2 mb-3 bg-gray-500 text-white rounded hover:bg-gray-600">
        Back to Dashboard
      </button>

      {/* Error Message */}
      {error && <p className="mt-4 p-2 bg-red-600 text-white rounded">{error}</p>}

      {/* PnL Table */}
      {pnlData.length > 0 ? (
        <div className="overflow-x-auto">
          <table className="min-w-full border border-gray-600 shadow-lg text-center">
            <thead>
              <tr className="bg-gray-700 text-yellow-400">
                {["Order ID", "Symbol", "Buy Price", "Current Price", "Quantity", "PnL %", "Sell"].map((header, index) => (
                  <th key={index} className="border border-gray-600 p-3 text-sm">{header}</th>
                ))}
              </tr>
            </thead>
            <tbody>
  {pnlData?.length > 0 ? (
    pnlData
      .filter(order => order && order.orderId) // âœ… Ensure order & orderId exist
      .map((order, index) => (
        <tr key={index} className="bg-gray-900 hover:bg-gray-700 transition">
          <td className="border border-gray-600 p-2 text-xs">{order.orderId}</td>
          <td className="border border-gray-600 p-2 text-xs font-bold">{order.symbol || "N/A"}</td>
          <td className="border border-gray-600 p-2 text-xs text-green-400">
            ${order.buyPrice ? order.buyPrice.toFixed(2) : "0.00"}
          </td>
          <td className="border border-gray-600 p-2 text-xs text-blue-400">
            ${order.currentPrice ? order.currentPrice.toFixed(2) : "0.00"}
          </td>
          <td className="border border-gray-600 p-2 text-xs">{order.quantity?.toFixed(3) || "N/A"}</td>
          <td className="border border-gray-600 p-2 text-xs font-bold">
            {order.pnlPercentage ? order.pnlPercentage.toFixed(2) : "0.00"}%
          </td>

          {/* âœ… Prevent Sell on Invalid Data */}
          <td className="border border-gray-600 p-2">
            {order.orderId ? (
              <button
                onClick={() => handleSellOrder(order.symbol, order.orderId)}
                className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600"
                disabled={sellingOrder === order.orderId}
              >
                {sellingOrder === order.orderId ? "Selling..." : "Sell"}
              </button>
            ) : (
              <span className="text-gray-400">N/A</span>
            )}
          </td>
        </tr>
      ))
  ) : (
    <tr>
      <td colSpan="7" className="text-center text-gray-400 p-3">
        No active orders found.
      </td>
    </tr>
  )}
</tbody>

          </table>
        </div>
      ) : (
        <p className="text-center text-gray-400">No active orders found.</p>
      )}
    </div>
  );
};

export default PnLReport;
